const crypto = require('./src')
const {TextEncoder, TextDecoder} = require('text-encoding')

let data = new TextEncoder().encode('foo')

console.log('DATA', data)

crypto.subtle
  .digest({ name: 'SHA-512' }, data)
  .then(digest => {
    console.log('DIGEST', digest, new Buffer(new TextDecoder().decode(digest), 'base64'))
  })
  .catch(console.log)

